// Import React, useEffect, useState, and useRef
import React, { useEffect, useState, useRef } from "react";
import Chart from "chart.js/auto";
import { Sidebar } from "flowbite-react";

// NavigationBar component
const NavigationBar = () => {
  return (
    <nav className="bg-white p-4 text-black">
      {/* Navigation items */}
      <ul className="flex justify-between">
        <li>
          <nav />
        </li>
      </ul>
    </nav>
  );
};

// Dashboard component
const Dashboard = () => {
  // State to store the full stock level
  const [fullStockLevel, setFullStockLevel] = useState(null);

  // Function to fetch full stock level from the database
  const fetchFullStockLevel = async () => {
    try {
      // Example: Fetch full stock level from a backend server endpoint
      const response = await fetch("/api/fullStockLevel"); // Adjust the endpoint URL accordingly
      if (!response.ok) {
        throw new Error("Failed to fetch full stock level");
      }
      const data = await response.json();
      setFullStockLevel(data.fullStockLevel);
    } catch (error) {
      console.error("Error fetching full stock level:", error.message);
    }
  };

  useEffect(() => {
    fetchFullStockLevel(); // Fetch full stock level when the component mounts
  }, []);

  // Rest of your component code...

  return (
    <div className="py-10 my-12">
      {/* Include the NavigationBar component */}
      <NavigationBar />
      {/* Display Full Stock Level */}
      <div className="flex justify-center items-center mb-4">
        <h2 className="text-2xl font-semibold">
          Full Stock Level: {fullStockLevel}
        </h2>
      </div>
      {/* Dashboard content */}
      {/* Rest of your component code... */}
    </div>
  );
};

export default Dashboard;
