import React, { useState, useEffect } from "react";
import { Button, Label, TextInput, Textarea } from "flowbite-react";

const UploadAccessories = () => {
  const category = [
    "Computer Accessories",
    "Laptop Accessories",
    "CCTV Accessories",
  ];

  const [selectedCategory, setSelectedCategory] = useState(category[0]);
  const [totalQuantity, setTotalQuantity] = useState(
    () => parseInt(localStorage.getItem("totalQuantity")) || 0
  );
  const [outOfStockCount, setOutOfStockCount] = useState(
    () => parseInt(localStorage.getItem("outOfStockCount")) || 0
  );
  const [totalValue, setTotalValue] = useState(
    () => parseFloat(localStorage.getItem("totalValue")) || 0
  );

  useEffect(() => {
    // Fetch accessories data from the backend when the component mounts
    fetch("http://localhost:8000/accessories")
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to fetch accessories.");
        }
        return res.json();
      })
      .then((data) => {
        // Calculate totalQuantity, outOfStockCount, and totalValue
        let newTotalQuantity = 0;
        let newOutOfStockCount = 0;
        let newTotalValue = 0;

        data.forEach((accessory) => {
          newTotalQuantity += parseInt(accessory.Quantity);
          newTotalValue +=
            parseInt(accessory.Quantity) * parseFloat(accessory.Price_per_unit);
          if (parseInt(accessory.Quantity) === 0) {
            newOutOfStockCount++;
          }
        });

        // Update state and local storage
        setTotalQuantity(newTotalQuantity);
        setOutOfStockCount(newOutOfStockCount);
        setTotalValue(newTotalValue);
        localStorage.setItem("totalQuantity", newTotalQuantity.toString());
        localStorage.setItem("outOfStockCount", newOutOfStockCount.toString());
        localStorage.setItem("totalValue", newTotalValue.toString());
      })
      .catch((error) => {
        console.error("Error fetching accessories:", error);
      });
  }, []);

  const handleChangeSelectedValue = (event) => {
    setSelectedCategory(event.target.value);
  };

  //Handle Accessories Submission
  const handleAccessoriesSubmit = (event) => {
    event.preventDefault();
    const form = event.target;

    const AccessoriesID = form.AccessoriesID.value;
    const AccessoriesName = form.AccessoriesName.value;
    const Category = form.Category.value;
    const Quantity = parseInt(form.Quantity.value);
    const Price_per_unit = parseFloat(form.Price_per_unit.value);
    const Description = form.Description.value;

    const accessoriesObj = {
      AccessoriesID,
      AccessoriesName,
      Category,
      Quantity,
      Price_per_unit,
      Description,
    };

    // Calculate totalValue
    const itemTotalValue = Quantity * Price_per_unit;
    const newTotalValue = totalValue + itemTotalValue;
    setTotalValue(newTotalValue);

    // Update totalQuantity
    const newTotalQuantity = totalQuantity + Quantity;
    setTotalQuantity(newTotalQuantity);

    // Update outOfStockCount
    const newOutOfStockCount =
      Quantity === 0 ? outOfStockCount + 1 : outOfStockCount;
    setOutOfStockCount(newOutOfStockCount);

    //send data to database
    fetch("http://localhost:8000/upload-accessories", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(accessoriesObj),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(
            "Failed to upload accessories. Please try again later."
          );
        }
        return res.json();
      })
      .then((data) => {
        alert("Accessories Uploaded Successfully");

        // Send calculated data to the backend
        const currentDate = new Date().toISOString().split("T")[0];
        const inventoryTotalData = {
          totalQuantity: newTotalQuantity,
          outOfStockCount: newOutOfStockCount,
          totalValue: newTotalValue,
          date: currentDate,
        };

        fetch("http://localhost:8000/inventory-total", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(inventoryTotalData),
        })
          .then((res) => {
            if (!res.ok) {
              throw new Error(
                "Failed to send inventory total data. Please try again later."
              );
            }
            return res.json();
          })
          .then((data) => {
            console.log("Inventory totals stored successfully:", data);
          })
          .catch((error) => {
            console.error("Error storing inventory totals:", error);
            alert(error.message); // Display the error message to the user
          });

        form.reset();
      })
      .catch((error) => {
        console.error("Error during fetch:", error);
        alert(error.message); // Display the error message to the user
      });
  };

  return (
    <div className="py-10 my-12">
      <h2 className="mb-8 text-3xl font-bold">Upload Accessories</h2>
      <form
        onSubmit={handleAccessoriesSubmit}
        className="flex lg:w-[1180px] flex-col flex-wrap gap-4"
      >
        <div className="flex gap-8">
          <div className="lg:w-1/2">
            <div className="mb-2 block">
              <Label htmlFor="AccessoriesID" value="Accessories ID" />
            </div>
            <TextInput
              id="AccessoriesID"
              name="AccessoriesID"
              type="text"
              placeholder="Enter Accessories ID"
              required
              className="w-full border border-gray-300 rounded-md focus:border-black"
            />
          </div>
          <div className="lg:w-1/2">
            <div className="mb-2 block">
              <Label htmlFor="AccessoriesName" value="Accessories Name" />
            </div>
            <TextInput
              id="AccessoriesName"
              name="AccessoriesName"
              type="text"
              placeholder="Enter Accessories Name"
              required
              className="w-full border border-gray-300 rounded-md focus:border-black"
            />
          </div>
        </div>

        <div className="flex gap-8">
          <div className="lg:w-1/2">
            <div className="mb-2 block">
              <Label htmlFor="Category" value="Category" />
            </div>
            <select
              id="Category"
              name="Category"
              className="w-full border border-gray-300 rounded-md focus:border-black"
              value={selectedCategory}
              onChange={handleChangeSelectedValue}
            >
              {category.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>
          <div className="lg:w-1/2">
            <div className="mb-2 block">
              <Label htmlFor="Quantity" value="Quantity" />
            </div>
            <TextInput
              id="Quantity"
              name="Quantity"
              type="text"
              placeholder="Enter Quantity"
              required
              className="w-full border border-gray-300 rounded-md focus:border-black"
            />
          </div>
        </div>

        <div className="flex gap-8">
          <div className="lg:w-1/2">
            <div className="mb-2 block">
              <Label htmlFor="Price_per_unit" value="Price Per Unit" />
            </div>
            <TextInput
              id="Price_per_unit"
              name="Price_per_unit"
              type="text"
              placeholder="Enter Price Per Unit"
              className="w-full border border-gray-300 rounded-md focus:border-black"
            />
          </div>
          <div className="lg:w-1/2">
            <div className="mb-2 block">
              <Label htmlFor="Description" value="Description" />
            </div>
            <Textarea
              id="Description"
              name="Description"
              type="text"
              placeholder="Enter Description"
              required
              rows={5}
              className="w-full border border-gray-300 rounded-md focus:border-black"
            />
          </div>
        </div>

        <button
          type="submit"
          className="mt-5 bg-blue-500 text-white py-2 px-4 rounded-md"
        >
          Upload Accessories
        </button>
      </form>
      <div>
        <p>Total Quantity: {totalQuantity}</p>
        <p>Out of Stock Count: {outOfStockCount}</p>
        <p>Total Value: {totalValue}</p>
      </div>
    </div>
  );
};

export default UploadAccessories;
