// Import React, useEffect, useState, and useRef
import React, { useEffect, useState, useRef } from "react";
import Chart from "chart.js/auto";
import { Sidebar } from "flowbite-react";

// NavigationBar component
const NavigationBar = () => {
  return (
    <nav className="bg-white p-4 text-black">
      {/* Navigation items */}
      <ul className="flex justify-between">
        <li>
          <nav />
        </li>
      </ul>
    </nav>
  );
};

// Dashboard component
const Dashboard = () => {
  // Sample data
  const stockLevel = 100;
  const outOfStockCount = 20;
  const categoryCount = 3;
  const stockValue = "$100";

  // Sample sales data for 12 months
  const salesData = [
    500, 600, 700, 800, 900, 1000, 1100, 1200, 1300, 1400, 1500, 1600,
  ];
  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  // State to store Inventory_Total data
  const [inventoryTotal, setInventoryTotal] = useState(null);

  // Function to fetch Inventory_Total data
  const fetchInventoryTotal = () => {
    // Example: Fetch or calculate Inventory_Total data
    const total = 1000; // Example value
    setInventoryTotal(total);
  };

  useEffect(() => {
    fetchInventoryTotal();
  }, []);

  // Rest of your component code...

  return (
    <div className="py-10 my-12">
      {/* Include the NavigationBar component */}
      <NavigationBar />
      {/* Inventory Total */}
      <div className="flex justify-center items-center mb-4">
        <h2 className="text-2xl font-semibold">
          Inventory Total: {inventoryTotal}
        </h2>
      </div>
      {/* Dashboard content */}
      {/* Rest of your component code... */}
    </div>
  );
};

export default Dashboard;
