import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Sidebar } from "flowbite-react";
import Dashboard from "./Dashboard"; // Import the Dashboard component here

const ManageAccessories = () => {
  // State variables
  const [accessories, setAccessories] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [fullStockLevel, setFullStockLevel] = useState(0);
  const [outOfStockItems, setOutOfStockItems] = useState(0);
  const [fullTotalValue, setFullTotalValue] = useState(0);

  // Fetch accessories data from the server
  useEffect(() => {
    const fetchAccessories = async () => {
      try {
        const response = await axios.get("/api/accessories");
        setAccessories(response.data);
      } catch (error) {
        console.error("Error fetching accessories:", error);
      }
    };

    fetchAccessories();
  }, []);

  // Calculate total stock level, out of stock items, and total value
  useEffect(() => {
    let totalStockLevel = 0;
    let outOfStockCount = 0;
    let totalValue = 0;

    accessories.forEach((accessory) => {
      totalStockLevel += accessory.Quantity || 0;
      if (accessory.Quantity === 0) {
        outOfStockCount++;
      }
      if (accessory.Quantity && accessory.Price_per_unit) {
        totalValue += accessory.Quantity * accessory.Price_per_unit;
      }
    });

    setFullStockLevel(totalStockLevel);
    setOutOfStockItems(outOfStockCount);
    setFullTotalValue(totalValue);
  }, [accessories]);

  // Handle date filter change
  const handleDateChange = (type, value) => {
    if (type === "start") {
      setStartDate(value);
    } else if (type === "end") {
      setEndDate(value);
    }
  };

  // Filter accessories based on search term and date range
  const filteredAccessories = accessories.filter((accessory) => {
    const matchSearchTerm =
      accessory.AccessoriesName.toLowerCase().includes(
        searchTerm.toLowerCase()
      ) ||
      accessory.Description.toLowerCase().includes(searchTerm.toLowerCase());

    const matchDateRange =
      (!startDate || new Date(accessory.updatedAt) >= new Date(startDate)) &&
      (!endDate || new Date(accessory.updatedAt) <= new Date(endDate));

    return matchSearchTerm && matchDateRange;
  });

  // Function to generate PDF
  const generatePDF = () => {
    // Logic to generate PDF
  };

  // Function to handle accessory deletion
  const handleDelete = async (id) => {
    // Logic to delete accessory
  };

  return (
    <div className="py-10 my-12">
      <h2 className="mb-8 text-3xl font-bold">Manage Accessories</h2>

      {/* Search and filter section */}
      <div className="flex flex-wrap justify-between mb-4">
        {/* Search input */}
        <div className="flex items-center mb-4">
          <input
            type="text"
            placeholder="Search by name..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-md"
          />

          {/* Start date filter */}
          <input
            type="date"
            value={startDate}
            onChange={(e) => handleDateChange("start", e.target.value)}
            className="ml-2 px-4 py-2 border border-gray-300 rounded-md"
          />

          {/* End date filter */}
          <input
            type="date"
            value={endDate}
            onChange={(e) => handleDateChange("end", e.target.value)}
            className="ml-2 px-4 py-2 border border-gray-300 rounded-md"
          />
        </div>

        {/* Download PDF button */}
        <button
          onClick={generatePDF}
          className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
        >
          Download PDF
        </button>
      </div>

      {/* Render the Dashboard component and pass props */}
      <Dashboard
        fullStockLevel={fullStockLevel}
        outOfStockItems={outOfStockItems}
        fullTotalValue={fullTotalValue}
      />

      {/* Table section */}
      <table className="lg:w-[1180px]">
        {/* Table header */}
        <thead className="bg-gray-200 dark:bg-gray-700">
          <tr>
            <th className="px-4 py-2">No</th>
            <th className="px-4 py-2">Accessories ID</th>
            <th className="px-4 py-2">Accessories Name</th>
            <th className="px-4 py-2">Quantity</th>
            <th className="px-4 py-2">Price Per Unit</th>
            <th className="px-4 py-2">Description</th>
            <th className="px-4 py-2">Last Updated</th>
            <th className="px-4 py-2">Total Value</th>
            <th className="px-4 py-2 text-right">Actions</th>
          </tr>
        </thead>
        {/* Table body */}
        <tbody>
          {/* Map through filteredAccessories and render each item */}
          {filteredAccessories.map((accessory, index) => (
            <tr
              className="bg-white dark:border-gray-700 dark:bg-gray-800 px-4 py-2"
              key={accessory._id}
            >
              {/* Table data cells */}
              <td className="text-center">{index + 1}</td>
              <td className="text-center">{accessory.AccessoriesID}</td>
              <td className="text-center">{accessory.AccessoriesName}</td>
              <td className="text-center">{accessory.Quantity}</td>
              <td className="text-center">{accessory.Price_per_unit}</td>
              <td className="text-center">{accessory.Description}</td>
              <td className="text-center">
                {accessory.updatedAt
                  ? new Date(accessory.updatedAt).toLocaleDateString()
                  : ""}
              </td>
              <td className="text-center">
                {accessory.Quantity && accessory.Price_per_unit
                  ? accessory.Quantity * accessory.Price_per_unit
                  : ""}
              </td>
              <td className="text-right">
                {/* Edit button */}
                <Link
                  to={`/admin/dashboard/edit-accessories/${accessory._id}`}
                  className="text-cyan-600 hover:underline dark:text-cyan-500 mr-6"
                >
                  Edit
                </Link>
                {/* Delete button */}
                <button
                  onClick={() => handleDelete(accessory._id)}
                  className="bg-red-600 text-white px-4 py-1 font-semibold rounded-sm hover:bg-sky-600"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageAccessories;
