import React, { useEffect, useState, useRef } from "react";
import Chart from "chart.js/auto";

const Dashboard = () => {
  // Sample data
  const stockLevel = 100;
  const outOfStockCount = 20;
  const categoryCount = 3;
  const stockValue = "$100";

  // Sample sales data for 12 months
  const salesData = [
    500, 600, 700, 800, 900, 1000, 1100, 1200, 1300, 1400, 1500, 1600,
  ];
  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const [topItemsData, setTopItemsData] = useState(null);

  const fetchTopItemsData = () => {
    const topItems = [
      { name: "Laptop", value: 1500 },
      { name: "Desktop", value: 1200 },
      { name: "Monitor", value: 800 },
      { name: "Keyboard", value: 500 },
      { name: "Mouse", value: 400 },
      { name: "Printer", value: 300 },
      { name: "Router", value: 200 },
      { name: "Speaker", value: 100 },
      { name: "Webcam", value: 50 },
      { name: "Headset", value: 30 },
    ];
    setTopItemsData(topItems);
  };

  useEffect(() => {
    fetchTopItemsData();
  }, []);

  useEffect(() => {
    if (topItemsData) {
      const labels = topItemsData.map((item) => item.name);
      const values = topItemsData.map((item) => item.value);

      const chartConfig = {
        type: "line",
        data: {
          labels: labels,
          datasets: [
            {
              label: "Top Items",
              data: values,
              fill: false,
              borderColor: "#3e95cd",
              borderWidth: 2,
            },
          ],
        },
        options: {
          responsive: true,
          maintainAspectRatio: false,
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        },
      };

      const topItemsLineChartRef = document.getElementById("topItemsLineChart");
      if (topItemsLineChartRef) {
        new Chart(topItemsLineChartRef, chartConfig);
      }
    }
  }, [topItemsData]);

  // Refs for the canvas elements to render the charts
  const chartRef = useRef(null);
  const pieChartRef = useRef(null);
  const pieChartBoxRef = useRef(null);
  const categoryPieChartRef = useRef(null); 

  // Categories
  const categories = {
    "Computer Accessories": 10,
    "Laptop Accessories": 5,
    "CCTV Accessories": 7,
  };

  // Create pie chart data for categories
  const categoryData = {
    labels: Object.keys(categories),
    datasets: [
      {
        label: "Category",
        data: Object.values(categories),
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
        ],
        borderWidth: 1,
      },
    ],
  };

  // Chart.js configuration for category pie chart
  const categoryPieChartConfig = {
    type: "pie",
    data: categoryData,
  };

  useEffect(() => {
    if (categoryPieChartRef.current) {
      const categoryPieChart = new Chart(
        categoryPieChartRef.current,
        categoryPieChartConfig
      );
      return () => {
        categoryPieChart.destroy(); 
      };
    }
  }, [categoryPieChartConfig]);

  useEffect(() => {
    const chartConfig = {
      type: "bar",
      data: {
        labels: months, 
        datasets: [
          {
            label: "Sales",
            data: salesData,
            backgroundColor: "#4CAF50",
            borderColor: "transparent",
            borderWidth: 0,
          },
        ],
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    if (chartRef.current) {
      const myChart = new Chart(chartRef.current, chartConfig);
      return () => {
        myChart.destroy(); 
      };
    }
  }, []);

  const pieChartConfig = {
    type: "pie",
    data: {
      labels: ["In Stock", "Out of Stock"],
      datasets: [
        {
          label: "Stock Status",
          data: [stockLevel, outOfStockCount],
          backgroundColor: [
            "rgba(75, 192, 192, 0.2)",
            "rgba(255, 99, 132, 0.2)",
          ],
          borderColor: ["rgba(75, 192, 192, 1)", "rgba(255, 99, 132, 1)"],
          borderWidth: 1,
        },
      ],
    },
  };

  useEffect(() => {
    if (pieChartRef.current && pieChartBoxRef.current) {
      const pieChart = new Chart(pieChartRef.current, pieChartConfig);
      const chartCanvas = chartRef.current;
      const chartBounds = chartCanvas.getBoundingClientRect();
      const boxSize = Math.min(chartBounds.width, chartBounds.height);
      pieChartBoxRef.current.style.width = `${boxSize}px`;
      pieChartBoxRef.current.style.height = `${boxSize}px`;
      return () => {
        pieChart.destroy(); 
      };
    }
  }, [pieChartConfig]);

  return (
    <div style={{ padding: "10px" }}>
      <nav style={{ backgroundColor: "#ffffff", padding: "16px", color: "#000000" }}>
        <ul style={{ display: "flex", justifyContent: "space-between" }}>
          <li><nav /></li>
        </ul>
      </nav>
      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "space-between" }}>
        <div style={{ padding: "16px", borderRadius: "8px", marginBottom: "16px", flexGrow: 1, backgroundColor: "#4CAF50" }}>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>Stock Level</h2>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{stockLevel}</p>
        </div>
        <div style={{ padding: "16px", borderRadius: "8px", marginBottom: "16px", flexGrow: 1, backgroundColor: "#FF0000" }}>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>Out of Stock</h2>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{outOfStockCount}</p>
        </div>
        <div style={{ padding: "16px", borderRadius: "8px", marginBottom: "16px", flexGrow: 1, backgroundColor: "#FFA500" }}>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>Categories</h2>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{categoryCount}</p>
        </div>
        <div style={{ padding: "16px", borderRadius: "8px", marginBottom: "16px", flexGrow: 1, backgroundColor: "#0000FF" }}>
          <h2 style={{ fontSize: "18px", fontWeight: "bold" }}>Stock Value</h2>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{stockValue}</p>
        </div>
      </div>
      {/* More rows and components */}
    </div>
  );
};

export default Dashboard;
