import React, { useState } from 'react';
import Chart from 'chart.js/auto';

// NavigationBar component
const NavigationBar = () => {
  return (
    <nav className="bg-white p-4 text-black">
      <ul className="flex justify-between">
        {/* Navigation items will go here */}
      </ul>
    </nav>
  );
};

// Dashboard component
const Dashboard = ({
  fullStockLevel,
  outOfStockItems = 0,
  fullTotalValue,
}) => {
  // It's good practice to validate prop types or provide default values
  console.log("fullStockLevel:", fullStockLevel);
  console.log("outOfStockItems:", outOfStockItems);
  console.log("fullTotalValue:", fullTotalValue);

  return (
    <div>
      <NavigationBar />
      <div>
        <p>Full Stock Level: {fullStockLevel}</p>
        <p>Out of Stock Items: {outOfStockItems}</p>
        <p>Full Total Value: {fullTotalValue}</p>
      </div>
    </div>
  );
};

// Parent component
const App = () => {
  // State to hold the values
  const [stockInfo, setStockInfo] = useState({
    fullStockLevel,
    outOfStockItems,
    fullTotalValue
  });

  // Pass the state values as props to the Dashboard component
  return (
    <div>
      <Dashboard
        fullStockLevel={stockInfo.fullStockLevel}
        outOfStockItems={stockInfo.outOfStockItems}
        fullTotalValue={stockInfo.fullTotalValue}
      />
    </div>
  );
};


export default App;
