import React, { useState, useEffect } from "react";
import { Button, Label, TextInput, Textarea } from "flowbite-react";

const UploadAccessories = () => {
  const category = [
    "Computer Accessories",
    "Laptop Accessories",
    "CCTV Accessories",
  ];

  const [selectedCategory, setSelectedCategory] = useState(category[0]);
  const [totalQuantity, setTotalQuantity] = useState(0);
  const [outOfStockCount, setOutOfStockCount] = useState(0);
  const [totalValue, setTotalValue] = useState(0);

  useEffect(() => {
    // Fetch initial values from the backend when the component mounts
    fetchInitialValues();
  }, []);

  useEffect(() => {
    const filtered = allAccessories.filter((accessory) => {
      const nameMatch =
        accessory?.AccessoriesName &&
        accessory.AccessoriesName.toLowerCase().includes(
          searchTerm.toLowerCase()
        );
      const dateMatch =
        (!startDate || accessory?.updatedAt >= startDate) &&
        (!endDate || accessory?.updatedAt <= endDate);
      return nameMatch && dateMatch;
    });

    setFilteredAccessories(filtered);

    let totalQuantity = 0;
    let totalValue = 0;
    let outOfStockCount = 0;
    filtered.forEach((accessory) => {
      totalQuantity += parseInt(accessory?.Quantity);
      totalValue +=
        parseInt(accessory?.Quantity) * parseFloat(accessory?.Price_per_unit);
      if (parseInt(accessory?.Quantity) === 0) {
        outOfStockCount++;
      }
    });
    setFullStockLevel(totalQuantity);
    setOutOfStockItems(outOfStockCount);
    setFullTotalValue(totalValue);
  }, [allAccessories, searchTerm, startDate, endDate]);

  useEffect(() => {
    if (!dataSent) {
      const currentDate = new Date().toISOString().split("T")[0];
      const data = {
        totalQuantity: fullStockLevel,
        outOfStockCount: outOfStockItems,
        totalValue: fullTotalValue,
        date: currentDate,
      };

      axios
        .post("http://localhost:8000/inventory-total", data)
        .then((response) => {
          console.log("Inventory totals stored successfully:", response.data);
          setDataSent(true); // Set dataSent to true after successful storage
        })
        .catch((error) => {
          console.error("Error storing inventory totals:", error);
        });
    }
  }, [fullStockLevel, outOfStockItems, fullTotalValue, dataSent]);

  const fetchInitialValues = () => {
    fetch("http://localhost:8000/initial-values") // Replace with your backend endpoint
      .then((res) => {
        if (!res.ok) {
          throw new Error("Failed to fetch initial values.");
        }
        return res.json();
      })
      .then((data) => {
        // Set initial values fetched from the database
        setTotalQuantity(data.totalQuantity);
        setOutOfStockCount(data.outOfStockCount);
        setTotalValue(data.totalValue);
      })
      .catch((error) => {
        console.error("Error fetching initial values:", error);
      });
  };

  // Rest of your component code...

  return (
    <div className="py-10 my-12">
      {/* Your component JSX */}
      <div>
        <p>Total Quantity: {totalQuantity}</p>
        <p>Out of Stock Count: {outOfStockCount}</p>
        <p>Total Value: {totalValue}</p>
      </div>
    </div>
  );
};

export default UploadAccessories;
